// Must be a reusable component that ingests JSON data as props and renders a single instance for each project
//Must utilize Router props to properly render the right project based on user selection
//Must render the following info:
// Project title
// Link to the deployed version
// Link to the GitHub repository
// GIF or screenshot of the deployed application
// this is an actual card content
import React from "react";
// import ContentCard from "../components/ContentCard";
import Layout from "../components/Layout";
import projectData from "../details.json";
import { Link } from "react-router-dom";

// import "../pages/ProjectGallery.css";

import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
// i will create one instance of a card and populate it with appropriate content from my project JSON
function ProjectGallery() {
  const [selectProject, setSelectProject] = useState(null);
  //getting the data from previous page to get the user selection
  useEffect(() => {
    const clickedProject = localStorage.getItem("selectedProject");
    setSelectProject(parseInt(clickedProject));
  }, []);

  const handleProjectClick = (projectId) => {
    setSelectedProject(projectId);
    localStorage.setItem("selectedProjectId", projectId);
  };

  const filteredProjects = selectProject
    ? projectData.filter((project) => project.id === selectProject)
    : projectData;

  return (
    <div>
      <Layout>
        <Row xs={1} md={2} className="g-4">
          {projectData.map((project) => (
            <Col key={project.id}>
              <div>{project.title}</div>
              <div>{project.description}</div>
              <div>
                <Link>{project.projectUrl}</Link>
              </div>
              <div>
                <Link>{project.repoUrl}</Link>
              </div>

              <Card
                className="projectsCards"
                onClick={() => handleProjectClick(project.id)}
              >
                <Card.Img
                  className="projectsImgEl"
                  variant="top"
                  src={process.env.PUBLIC_URL + "/" + project.projectImage}
                  alt={project.title}
                />
              </Card>
            </Col>
          ))}
        </Row>
      </Layout>
    </div>
  );
}

export default ProjectGallery;
