// this is where json file data from porjects comes in. Reusable component// dont forget to import it
// render six instances of projectgallery projects
import React from "react";
import Layout from "../components/Layout";
import projectData from "../details.json";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
// import {Link} from  "react-router-dom"
import "../pages/Projects.css";

const Projects = () => {
  return (
    <div className="projectsBody">
      <Layout>
        <h1>Just pick the project you would like to see </h1>
        <Row xs={1} md={2} className="g-4">
          {projectData.map((project) => (
            <Col key={project.id}>
              <div onClick={() => expandProject(project)}>
                <Card className="projectsCards">
                  <Card.Img
                    className="projectsImgEl"
                    variant="top"
                    src={process.env.PUBLIC_URL + "/" + project.projectImage}
                    alt={project.title}
                  />
                </Card>
              </div>
            </Col>
          ))}
        </Row>
      </Layout>
    </div>
  );
};

function expandProject(project) {
  console.log("lalalal");
}
expandProject();

export default Projects;
