import React from "react";
import Layout from "../components/Layout";
import "../pages/About.css";
// import Card from "react-bootstrap/Card";
import ContentCard from "../components/ContentCard";

const About = () => {
  return (
    <div>
      <Layout>
        <div className="aboutContainer">
          <div className="row pic">
            <img src={`${process.env.PUBLIC_URL}/lola.jpeg`} alt="Avatar" />
          </div>

          <div className="aboutContEl row">
            <ContentCard title="A little bit about me" />
            <p
              style={{ margin: "10px", fontFamily: "sofia", fontSize: "20px" }}
            >
              It is so nice to meet you ! I'm Lola Kasimova - an
              empathetic,highly sensitive and fun loving human who loves
              meaningful conversations, life changing ideas and artistic
              expression.<br></br>
              <br></br>Supporting others is the driving force behind the idea of
              my dream career. I strive to broaden the integration of social
              work into my own work and the arts curriculum by tackling social
              issues through real life projects and sparking the inherent need
              for cultural understanding. I believe it is never too late to
              pursue your absolute biggest aspirations.<b></b> I believe that
              art can solve social issues as well as make others aware of their
              existence. Social work and the arts work beautifully together in
              nurturing connection and inclusivity amongst people and
              communities. I will never stop fighting to build a brighter world
              for future generations. "
            </p>
          </div>
        </div>
      </Layout>
    </div>
  );
};

export default About;
