import React from "react";
import Layout from "../components/Layout";
import "../pages/About.css";
// import Card from "react-bootstrap/Card";
import ContentCard from "../components/ContentCard";

const About = () => {
  return (
    <div>
      <Layout>
        <div className="aboutContainer">
          <div className="row pic">
            <img src={`${process.env.PUBLIC_URL}/lola.jpeg`} alt="Avatar" />
          </div>

          <div className="aboutContEl row">
            <ContentCard title="A little bit about me" />
            <ContentCard
              content=" Hello, it is so nice to meet you ! I'm Lola Kasimova - an empathetic,highly sensitive human and fun loving human who loves meaningful conversatiions, life changing ideas and artistic expression. \n\n



Supporting others is the driving force behind the work that I do and I create. I strive to broaden the integration of social work into my own work and the arts curriculum by tackling social issues through real life projects and sparking the inherent need for cultural understanding. 

I believe it is never too late to pursue your absolute biggest aspirations, and I thank my daughters, husband, and my students for pushing me to grow as I begin my newest journey as a graduate student at the University of Pennsylvania in the MSW (Master of Social Work) program. I believe that art can solve social issues as well as make others aware of their existence. Social work and the arts work beautifully together in nurturing connection and inclusivity amongst people and communities.

I will never stop fighting to build a brighter world for future generations. "
            />
          </div>
        </div>
      </Layout>
    </div>
  );
};

export default About;
